package com.company.Stekove_Opashki;import java.lang.reflect.Array;import java.text.DecimalFormat;import java.util.ArrayDeque;import java.util.Scanner;public class DecimalToBinaryConverter_03 {    public static void main(String[] args) {        Scanner scanner = new Scanner(System.in);        int number = Integer.parseInt(scanner.nextLine());        ArrayDeque<Integer> binaryFormat = new ArrayDeque<>();        if (number == 0) {            System.out.println(0);        } else {            while (number != 0) {                int currentNumber = number % 2;                binaryFormat.push(currentNumber);                number = number / 2;            }            binaryFormat.forEach(System.out::print);        }    }}