package com.company.Stekove_Opashki;import java.util.ArrayDeque;import java.util.Arrays;import java.util.Scanner;public class MatchingBrackets_04 {    public static void main(String[] args) {        Scanner scanner = new Scanner(System.in);        String input = scanner.nextLine();        String[] matchingBrackets = input.split(" ");        ArrayDeque<Integer> result = new ArrayDeque<>();        for (int i = 0; i < input.length(); i++) {            char currentSymbol = input.charAt(i);            if (currentSymbol == '(') {                result.push(i);            } else if (currentSymbol == ')') {                int startIndex = result.pop();                int endIndex = i;                System.out.println(input.substring(startIndex, endIndex + 1));            }        }    }}