package com.company.Stekove_Opashki;import java.util.ArrayDeque;import java.util.Scanner;public class SimpleCalculator_02 {    public static void main(String[] args) {        Scanner scanner = new Scanner(System.in);        String input = scanner.nextLine();        String[] splitInput = input.split("\\s+");        String command = "";        int number = 0;        int result = 0;        for (int i = 0; i < splitInput.length; i++) {            String currentCommand = splitInput[i];            if (i % 2 == 0 && i != 0) {                result = Integer.parseInt(currentCommand);                if (command.equals("-")){                    result = number - result;                    number = result;                    continue;                }else if (command.equals("+")){                    result = result + number;                    number = result;                    continue;                }            }            if (currentCommand.equals("-")) {                command = currentCommand;            } else if (currentCommand.equals("+")) {                command = currentCommand;            } else if (i == 0){                number = Integer.parseInt(currentCommand);            }        }        System.out.println(result);    }}