package com.company.Stekove_Opashki;import java.util.ArrayDeque;import java.util.Scanner;public class workingWithQueues_05 {    public static void main(String[] args) {        Scanner scanner = new Scanner(System.in);        String printFail = scanner.nextLine();        ArrayDeque<String> fail = new ArrayDeque<>();        while (!printFail.equals("print")){            if (printFail.equals("cancel")){                if (!fail.isEmpty()){                    System.out.printf("Canceled %s%n", fail.poll());                }else {                    System.out.println("Printer is on standby");                }            }else {                fail.offer(printFail);            }            printFail = scanner.nextLine();        }        while (!fail.isEmpty()){            System.out.println(fail.poll());        }    }}