package com.company.Stekove_Opashki._exersises;import java.util.ArrayDeque;import java.util.Arrays;import java.util.Scanner;public class BasicQueueOperations_04 {    public static void main(String[] args) {        Scanner scanner = new Scanner(System.in);        int[] input = Arrays.stream(scanner.nextLine().split("\\s+"))                .mapToInt(Integer::parseInt).toArray();        int n = input[0];        int s = input[1];        int x = input[2];        ArrayDeque<Integer> numbers = new ArrayDeque<>();        int[] inutNumbers = Arrays.stream(scanner.nextLine().split("\\s+"))                .mapToInt(Integer::parseInt).toArray();        for (int i = 0; i < n; i++) {            numbers.offer(inutNumbers[i]);        }        for (int i = 0; i < s; i++) {            numbers.poll();        }        if (numbers.isEmpty()){            System.out.println(0);        }else {            if (numbers.contains(x)){                System.out.println("true");            }else {                System.out.println(numbers.stream().mapToInt(e -> e).min().getAsInt());            }        }    }}