package com.company.Stekove_Opashki._exersises;import java.util.ArrayDeque;import java.util.Scanner;public class MaximumElements_03 {    public static void main(String[] args) {        Scanner scanner = new Scanner(System.in);        int n = Integer.parseInt(scanner.nextLine());        ArrayDeque<Integer> numbers = new ArrayDeque<>();        for (int i = 0; i < n; i++) {            String[] input = scanner.nextLine().split("\\s+");            String command = input[0];            switch (command) {                case "1" -> {                    int number = Integer.parseInt(input[1]);                    numbers.push(number);                }                case "2" -> numbers.pop();                case "3" -> System.out.println(numbers.stream().mapToInt(e -> e).max().getAsInt());            }        }    }}