package com.company.Stekove_Opashki._exersises;import java.util.Scanner;public class RecursiveFibonacci_07 {    public static long[] memoryFib ;    public static void main(String[] args) {        Scanner scanner = new Scanner(System.in);        int n = Integer.parseInt(scanner.nextLine());        memoryFib = new long[n + 1];        System.out.println(getFib(n));    }    private static long getFib(int n) {        if (n <= 1){            return 1;        }        if (memoryFib[n] != 0){            return memoryFib[n];        }        return memoryFib[n] = getFib(n - 1) + getFib( n -2);    }}