package com.company.Stekove_Opashki._exersises;import java.util.ArrayDeque;import java.util.Arrays;import java.util.Collections;import java.util.Scanner;public class ReverseNumberssWithStack_01 {    public static void main(String[] args) {        Scanner scanner = new Scanner(System.in);        int[] inputNumbers = Arrays.stream(scanner.nextLine().split("\\s+"))                .mapToInt(Integer::parseInt).toArray();        ArrayDeque<Integer> reverseNumbers = new ArrayDeque<>();        Arrays.stream(inputNumbers).forEach(reverseNumbers::push);        reverseNumbers.forEach(e -> System.out.print(e.toString().concat(" ")));    }}