package com.company.Stekove_Opashki._exersises;import java.util.ArrayDeque;import java.util.Arrays;import java.util.Scanner;public class Robotics_05 {    public static void main(String[] args) {        Scanner scanner = new Scanner(System.in);        String input = scanner.nextLine().replaceAll(" ", "");        ArrayDeque<Character> que = new ArrayDeque<>();        ArrayDeque<Character> stack = new ArrayDeque<>();        if (input.charAt((input.length() + 1) / 2) == ' ') {            for (int i = 0; i < (input.length() + 1) / 2; i++) {                stack.push(input.charAt(i));            }        } else {            for (int i = 0; i < input.length() / 2; i++) {                stack.push(input.charAt(i));            }            for (int i = input.length() / 2; i < input.length(); i++) {                que.offer(input.charAt(i));            }        }        boolean isBalansed = true;        if (que.size() == stack.size()) {            for (int i = 0; i < que.size(); i++) {                char queChar = que.poll();                char stackChar = stack.pop();                if (queChar == ')' && stackChar == '(') {                    isBalansed = true;                } else if (queChar == ']' && stackChar == '[') {                    isBalansed = true;                } else if (queChar == '}' && stackChar == '{') {                    isBalansed = true;                } else if (queChar == ' ' && stackChar == ' ') {                    isBalansed = true;                } else {                    isBalansed = false;                    break;                }            }        } else {            isBalansed = false;        }        if (isBalansed) {            System.out.println("YES");        } else {            System.out.println("NO");        }    }}