package com.company.MATRICI._LAB;import java.util.Arrays;import java.util.Scanner;public class CompareMatrices_01 {    public static void main(String[] args) {        Scanner scanner = new Scanner(System.in);        int[][] firstMatrix = readMatrix(scanner);        int[][] secondMatrix = readMatrix(scanner);        boolean equalMatrix = isEqualMatrix(firstMatrix, secondMatrix);        if (equalMatrix) {            System.out.println("equal");        } else {            System.out.println("not equal");        }    }    public static int[][] readMatrix(Scanner scanner) {        int[] input = Arrays.stream(scanner.nextLine().split("\\s+"))                .mapToInt(Integer::parseInt).toArray();        int rows = input[0];        int cols = input[1];        int[][] matrix = new int[rows][];        for (int row = 0; row < rows; row++) {            int[] arr = Arrays.stream(scanner.nextLine().split("\\s+"))                    .mapToInt(Integer::parseInt).toArray();            matrix[row] = arr;        }        return matrix;    }    public static boolean isEqualMatrix(int[][] fisrtMatrix, int[][] secondMatrix) {        if (fisrtMatrix.length != secondMatrix.length) {            return false;        } else {            for (int row = 0; row < fisrtMatrix.length; row++) {                if (fisrtMatrix[row].length != secondMatrix[row].length) {                    return false;                }                for (int col = 0; col < fisrtMatrix[row].length; col++) {                    if (fisrtMatrix[row][col] != secondMatrix[row][col]) {                        return false;                    }                }            }            return true;        }    }}