package com.company.MATRICI._LAB;import java.util.Arrays;import java.util.Scanner;public class CompareMatrices_01_VTORO_RESHENIE {        public static void main(String[] args) {            Scanner scanner = new Scanner(System.in);            int[][] firstMatrix = readMatrix(scanner);            int[][] secondMatrix = readMatrix(scanner);            boolean equalMatrix = true;            if (firstMatrix.length != secondMatrix.length) {                equalMatrix = false;            } else {                for (int row = 0; row < firstMatrix.length; row++) {                    if (firstMatrix[row].length != secondMatrix[row].length) {                        equalMatrix = false;                        break;                    }                    for (int col = 0; col < firstMatrix[row].length; col++) {                        if (firstMatrix[row][col] == secondMatrix[row][col]) {                            equalMatrix = true;                        } else {                            equalMatrix = false;                            break;                        }                    }                    if (!equalMatrix) {                        break;                    }                }            }            if (equalMatrix){                System.out.println("equal");            }else {                System.out.println("not equal");            }        }        public static int[][] readMatrix(Scanner scanner) {            int[] input = Arrays.stream(scanner.nextLine().split("\\s+"))                    .mapToInt(Integer::parseInt).toArray();            int rows = input[0];            int cols = input[1];            int[][] matrix = new int[rows][];            for (int row = 0; row < rows; row++) {                int[] arr = Arrays.stream(scanner.nextLine().split("\\s+"))                        .mapToInt(Integer::parseInt).toArray();                matrix[row] = arr;            }            return matrix;        }    }