package com.company.MATRICI._LAB;import java.util.Scanner;public class FindTheRealQueen_07 {    public static void main(String[] args) {        Scanner scanner = new Scanner(System.in);        int size = 8;        char[][] matrix = fillMatrix(scanner,size);            }    private static char[][] fillMatrix(Scanner scanner, int size) {        char[][] matrix = new char[size][];        for (int row = 0; row < size ; row++) {            char[] symbols = scanner.nextLine().replaceAll("\\s+","").toCharArray();            matrix[row] = symbols;        }        return  matrix;    }}