package com.company.MATRICI._LAB;import java.util.Arrays;import java.util.Scanner;public class IntersectionOfTwoMatrices_03 {    public static void main(String[] args) {        Scanner scanner = new Scanner(System.in);        int rows = Integer.parseInt(scanner.nextLine());        int cols = Integer.parseInt(scanner.nextLine());        char[][] firstMatrix = fillMatrix(scanner, rows, cols);        char[][] secondsMatrix = fillMatrix(scanner, rows, cols);        for (int row = 0; row < rows; row++) {            for (int col = 0; col < cols; col++) {                if (firstMatrix[row][col] == secondsMatrix[row][col]) {                    System.out.print(firstMatrix[row][col] + " ");                } else {                    System.out.print("*" + " ");                }            }            System.out.println();        }    }    public static char[][] fillMatrix(Scanner scanner, int rows, int cols) {        char[][] matrix = new char[rows][cols];        for (int row = 0; row < rows; row++) {            String[] symbols = scanner.nextLine().split("\\s+");            for (int col = 0; col < cols; col++) {                matrix[row][col] = symbols[col].charAt(0);            }        }        return matrix;    }}