package com.company.MATRICI._LAB;import java.util.Arrays;import java.util.Scanner;import java.util.stream.Collectors;import java.util.stream.Stream;public class MaximumSumOf2X2Submatrix_05 {    public static void main(String[] args) {        Scanner scanner = new Scanner(System.in);        int[][] matrix = readMatrix(scanner);        int sum = Integer.MIN_VALUE;        int[][] bestMatrix = new int[2][2];        for (int row = 0; row < matrix.length - 1; row++) {            for (int col = 0; col < matrix[row].length - 1; col++) {                int currentSum = matrix[row][col] + matrix[row][col + 1]                        + matrix[row + 1][col] + matrix[row + 1][col + 1];                if (currentSum > sum) {                    sum = currentSum;                   bestMatrix[0][0] =  matrix[row][col];                   bestMatrix[0][1]= matrix[row][col + 1];                   bestMatrix[1][0] = matrix[row + 1][col];                   bestMatrix[1][1] = matrix[row + 1][col + 1];                }            }        }        Arrays.stream(bestMatrix).forEach(i -> {            Arrays.stream(i).forEach(j -> System.out.print(j + " "));            System.out.println();        });        System.out.println(sum);    }    public static int[][] readMatrix(Scanner scanner) {        String[] input = scanner.nextLine().split(", ");        int rows = Integer.parseInt(input[0]);        int cols = Integer.parseInt(input[1]);        int[][] matrix = new int[rows][cols];        for (int row = 0; row < rows; row++) {            int[] arr = Arrays.stream(scanner.nextLine().split(", "))                    .mapToInt(Integer::parseInt).toArray();            matrix[row] = arr;        }        return matrix;    }}