package com.company.MATRICI._LAB;import java.util.Arrays;import java.util.Scanner;public class PositionsOf_02 {    public static void main(String[] args) {        Scanner scaner = new Scanner(System.in);        int[][] matrix = fillMatrix(scaner);        int searchNumber = Integer.parseInt(scaner.nextLine());        boolean isFaundNumber = false;        for (int row = 0; row < matrix.length; row++) {            for (int col = 0; col < matrix[row].length; col++) {                int currentNumber = matrix[row][col];                if (currentNumber == searchNumber) {                    System.out.println(row + " " + col);                    isFaundNumber = true;                }            }        }        if (!isFaundNumber) {            System.out.println("not found");        }    }    private static int[][] fillMatrix(Scanner scaner) {        int[] input = Arrays.stream(scaner.nextLine().split("\\s+"))                .mapToInt(Integer::parseInt).toArray();        int rows = input[0];        int cols = input[1];        int[][] matrix = new int[rows][];        for (int row = 0; row < rows; row++) {            int[] arr = Arrays.stream(scaner.nextLine().split("\\s+"))                    .mapToInt(Integer::parseInt).toArray();            matrix[row] = arr;        }        return matrix;    }}