package com.company.MATRICI._LAB;import java.util.Arrays;import java.util.Scanner;public class PrintDiagonalsOfSquareMatrix_06 {    public static void main(String[] args) {        Scanner scanner = new Scanner(System.in);        int size = Integer.parseInt(scanner.nextLine());        int[][] matrix = readMatrix(scanner, size);       int rows = 0;       int cols = 0;       while (rows < size && cols < size){           System.out.print(matrix[rows][cols] + " ");           rows++;           cols++;       }        System.out.println();       int row = size -1;       int col = 0;       while (row >= 0 && col <= size -1){           System.out.print(matrix[row][col] + " ");           row--;           col++;       }    }    private static int[][] readMatrix(Scanner scanner, int n) {        int[][] matrix = new int[n][];        for (int row = 0; row < n; row++) {            matrix[row] = Arrays.stream(scanner.nextLine().split("\\s+"))                    .mapToInt(Integer::parseInt).toArray();        }        return matrix;    }}