package com.company.MATRICI._LAB;import java.util.Arrays;import java.util.Scanner;public class SumMatrixElements_04 {    public static void main(String[] args) {        Scanner scanner = new Scanner(System.in);        String[] input = scanner.nextLine().split(", ");        int rows = Integer.parseInt(input[0]);        int cols = Integer.parseInt(input[1]);        int[][] matrix = readMatrix(scanner,rows,cols);        int sum = 0;        sum = sumElementsOfMatrix(matrix);        System.out.println(rows);        System.out.println(cols);        System.out.println(sum);    }    private static int sumElementsOfMatrix(int[][] matrix) {        int sum = 0;        for (int row = 0; row < matrix.length ; row++) {            for (int col = 0; col < matrix[row].length ; col++) {                sum+= matrix[row][col];            }        }        return sum;    }    public static int[][] readMatrix (Scanner scanner, int rows,int cols){        int[][] matrix = new int[rows][];        for (int row = 0; row < rows ; row++) {            int[] arr = Arrays.stream(scanner.nextLine().split(", "))                    .mapToInt(Integer::parseInt).toArray();            matrix[row] = arr;        }        return  matrix;    }}