package com.company.MATRICI_EXERCISES;import java.util.Arrays;import java.util.Scanner;public class DiagonalDifference_03 {    public static void main(String[] args) {        Scanner scanner = new Scanner(System.in);        int size = Integer.parseInt(scanner.nextLine());        int[][] matrix = new int[size][size];        for (int row = 0; row < size; row++) {            matrix[row] = Arrays.stream(scanner.nextLine().split(" "))                    .mapToInt(Integer::parseInt)                    .toArray();        }        int sumFirstDiagonal = 0;        int sumSecondDiagonal = 0;        for (int row = 0; row < size; row++) {            sumFirstDiagonal += matrix[row][row];            sumSecondDiagonal += matrix[size - 1 - row][row];        }        int maxSumDiagonal = Math.max(sumFirstDiagonal,sumSecondDiagonal);        int minSumDiagonal = Math.min(sumFirstDiagonal,sumSecondDiagonal);        System.out.println(maxSumDiagonal - minSumDiagonal);    }}