package com.company.MATRICI_EXERCISES;import java.util.Arrays;import java.util.Scanner;public class FillTheMatrix_01 {    public static void main(String[] args) {        Scanner scanner = new Scanner(System.in);        String[] input = scanner.nextLine().split(", ");        int size = Integer.parseInt(input[0]);        String pattern = input[1];        if (pattern.equals("A")) {            int[][] matrix = fillMatrixPatternA(size);            Arrays.stream(matrix).forEach(i -> {                Arrays.stream(i).forEach(j -> System.out.print(j + " "));                System.out.println();            });        } else if (pattern.equals("B")) {            int[][] matrix = fillMatrixPatternB(size);            Arrays.stream(matrix).forEach(i -> {                Arrays.stream(i).forEach(j -> System.out.print(j + " "));                System.out.println();            });        }    }    public static int[][] fillMatrixPatternA(int size) {        int[][] matrix = new int[size][size];        int fillNumber = 1;        for (int col = 0; col < size; col++) {            for (int row = 0; row < size; row++) {                matrix[row][col] = fillNumber++;            }        }        return matrix;    }    public static int[][] fillMatrixPatternB(int size) {        int fillNumber = 1;        int[][] matrix = new int[size][size];        for (int col = 0; col < size; col++) {            if (col % 2 == 0) {                for (int row = 0; row < size; row++) {                    matrix[row][col] = fillNumber++;                }            } else {                for (int row = matrix.length - 1; row >= 0; row--) {                    matrix[row][col] = fillNumber++;                }            }        }        return matrix;    }}