package com.company.MATRICI_EXERCISES;import java.util.Arrays;import java.util.Scanner;public class MatrixOfPalindromes_02 {    public static void main(String[] args) {        Scanner scanner = new Scanner(System.in);        int[] input = Arrays.stream(scanner.nextLine().split("\\s+"))                .mapToInt(Integer::parseInt)                .toArray();        int rows = input[0];        int cols = input[1];        String[][] matrix = new String[rows][cols];        char symbol = 'a';        for (int row = 0; row < rows; row++) {            for (int col = 0; col < cols; col++) {                matrix[row][col] = "" + ((char) (symbol + row)) + ((char) (symbol + col + row)) + ((char) (symbol + row));            }        }        Arrays.stream(matrix).forEach(i -> {            Arrays.stream(i).forEach(j -> System.out.print(j + " "));            System.out.println();        });    }}