package com.company.MATRICI_EXERCISES;import java.util.Arrays;import java.util.Scanner;public class MatrixShuffling_05 {    public static void main(String[] args) {        Scanner scanner = new Scanner(System.in);        String[] input = scanner.nextLine().split("\\s+");        int rows = Integer.parseInt(input[0]);        int cols = Integer.parseInt(input[1]);        String[][] matrix = fillMatrix(scanner, rows, cols);        String tokens = scanner.nextLine();        while (!tokens.equals("END")) {            String[] currentInput = tokens.split("\\s+");            String command = currentInput[0];            if (currentInput.length != 5) {                System.out.println("Invalid input!");            } else if (command.equals("swap")) {                int row1 = Integer.parseInt(currentInput[1]);                int col1 = Integer.parseInt(currentInput[2]);                int row2 = Integer.parseInt(currentInput[3]);                int col2 = Integer.parseInt(currentInput[4]);                if (row1 > rows || col1 > cols || row2 > rows || col2 > cols) {                    System.out.println("Invalid input!");                } else {                    String currentValue = matrix[row1][col1];                    matrix[row1][col1] = matrix[row2][col2];                    matrix[row2][col2] = currentValue;                    Arrays.stream(matrix).forEach(i -> {                        Arrays.stream(i).forEach(j -> System.out.print(j + " "));                        System.out.println();                    });                }            }            tokens = scanner.nextLine();        }    }    private static String[][] fillMatrix(Scanner scanner, int rows, int cols) {        String[][] matrix = new String[rows][cols];        for (int row = 0; row < rows; row++) {            matrix[row] = scanner.nextLine().split("\\s+");        }        return matrix;    }}