package com.company.MATRICI_EXERCISES;import java.util.ArrayList;import java.util.Arrays;import java.util.List;import java.util.Scanner;public class StringMatrixRotation_06 {    public static void main(String[] args) {        Scanner scanner = new Scanner(System.in);        String rotateDegrees = scanner.nextLine();        int degrees = Integer.parseInt(rotateDegrees.split("[()]+")[1]) % 360;        String input = scanner.nextLine();        List<String> date = new ArrayList<>();        int size = Integer.MIN_VALUE;        while (!input.equals("END")) {            date.add(input);            if (size < input.length()) {                size = input.length();            }            input = scanner.nextLine();        }        int rows = date.size();        int cols = size;        char[][] matrix = new char[rows][cols];        for (int row = 0; row < rows; row++) {            for (int col = 0; col < cols; col++) {                if (col > date.get(row).length() - 1) {                    matrix[row][col] = ' ';                } else {                    char currSymbol = date.get(row).charAt(col);                    matrix[row][col] = currSymbol;                }            }        }        if (degrees == 90) {            for (int col = 0; col < cols; col++) {                for (int row = rows - 1; row >= 0; row--) {                    System.out.print(matrix[row][col]);                }                System.out.println();            }        } else if (degrees == 180) {            for (int row = rows - 1; row >= 0; row--) {                for (int col = cols - 1; col >= 0; col--) {                    System.out.print(matrix[row][col]);                }                System.out.println();            }        } else if (degrees == 270) {            for (int col = cols - 1; col >= 0; col--) {                for (int row = 0; row < rows ; row++) {                    System.out.print(matrix[row][col]);                }                System.out.println();            }        }else if (degrees == 0){            for (int row = 0; row < rows ; row++) {                for (int col = 0; col < cols; col++) {                    System.out.print(matrix[row][col]);                }                System.out.println();            }        }    }}