package com.company.SET_MAP;import java.text.DecimalFormat;import java.util.*;public class AcadamyGraduation_08 {    public static void main(String[] args) {        Scanner scanner = new Scanner(System.in);        int n = Integer.parseInt(scanner.nextLine());        Map<String, List<Double>> studentDate = new TreeMap<>();        for (int i = 0; i < n; i++) {            String name = scanner.nextLine();            double[] grades = Arrays.stream(scanner.nextLine().split("\\s+"))                    .mapToDouble(Double::parseDouble).toArray();            double avgGrade = 0;            for (int j = 0; j < grades.length; j++) {                avgGrade += grades[j];            }            avgGrade = avgGrade / grades.length;            studentDate.putIfAbsent(name, new ArrayList<>());            studentDate.get(name).add(avgGrade);        }        studentDate.entrySet()                .stream()                .forEach( e -> {                    String name = e.getKey();                    e.getValue().stream().forEach(j ->                            System.out.printf("%s is graduated with %f%n",name,j));                });    }}