package com.company.SET_MAP;import java.util.*;public class CitiesByContinentAndCountry_07 {    public static void main(String[] args) {        Scanner scanner = new Scanner(System.in);        int n = Integer.parseInt(scanner.nextLine());        Map<String, Map<String, ArrayList<String>>> infoByContinents = new LinkedHashMap<>();        for (int i = 0; i < n; i++) {            String[] tokens = scanner.nextLine().split("\\s+");            String continent = tokens[0];            String country = tokens[1];            String city = tokens[2];            if (!infoByContinents.containsKey(continent)) {                infoByContinents.put(continent, new LinkedHashMap<>());                infoByContinents.get(continent).put(country, new ArrayList<>());                infoByContinents.get(continent).get(country).add(city);            } else {                if (!infoByContinents.get(continent).containsKey(country)) {                    infoByContinents.get(continent).put(country, new ArrayList<>());                    infoByContinents.get(continent).get(country).add(city);                } else {                    infoByContinents.get(continent).get(country).add(city);                }            }        }        infoByContinents.entrySet()                .forEach(e -> {                    System.out.printf("%s:%n", e.getKey());                    e.getValue().forEach((key, value) -> {                        System.out.println(key + " -> " + String.join(", ", value));                    });                });    }}