package com.company.SET_MAP;import java.util.LinkedHashMap;import java.util.Map;import java.util.Scanner;import java.util.TreeMap;public class ProductShop_06 {    public static void main(String[] args) {        Scanner scanner = new Scanner(System.in);        String input = scanner.nextLine();        Map<String, Map<String, Double>> infoShop = new TreeMap<>();        while (!input.equals("Revision")) {            String[] tokens = input.split(", ");            String shopName = tokens[0];            String productName = tokens[1];            double priceProduct = Double.parseDouble(tokens[2]);            infoShop.putIfAbsent(shopName, new LinkedHashMap<>());            infoShop.get(shopName).put(productName,priceProduct);            input = scanner.nextLine();        }        infoShop.entrySet()                .forEach(e -> {                    System.out.printf("%s->%n",e.getKey());                    e.getValue().entrySet().forEach(j ->                            System.out.printf("Product: %s, Price: %.1f%n",j.getKey(),j.getValue()));                });    }}