package com.company.SET_MAP;import java.util.*;import java.util.stream.Collectors;public class VoinaNumberGames_03 {    public static void main(String[] args) {        Scanner scanner = new Scanner(System.in);        Set<Integer> playerDeck1 = Arrays.stream(scanner.nextLine().split("\\s+"))                .map(Integer::parseInt).collect(Collectors.toCollection(LinkedHashSet::new));        Set<Integer> playerDeck2 = Arrays.stream(scanner.nextLine().split("\\s+"))                .map(Integer::parseInt).collect(Collectors.toCollection(LinkedHashSet::new));        int countGames = 50;        while (countGames > -1 ){            int cadrPlayer1 = playerDeck1.iterator().next();            playerDeck1.remove(cadrPlayer1);            int cardPlayer2 = playerDeck2.iterator().next();            playerDeck2.remove(cardPlayer2);            if (cadrPlayer1 > cardPlayer2){                playerDeck1.add(cadrPlayer1);                playerDeck1.add(cardPlayer2);            }else if (cadrPlayer1 < cardPlayer2){                playerDeck2.add(cardPlayer2);                playerDeck2.add(cadrPlayer1);            }            if ( playerDeck1.isEmpty() || playerDeck2.isEmpty()){                break;            }            countGames --;        }        if (playerDeck1.size() > playerDeck2.size()){            System.out.println("First player win!");        }else if (playerDeck1.size() < playerDeck2.size()){            System.out.println("Second player win!");        }else {            System.out.println("Draw!");        }    }}